mapper.select:
SELECT @RNUM := @RNUM + 1 AS ROWNUM, t.* FROM( SELECT /* boardQuery.contentsListByBoardId */ BBC.CONTENTS_ID , BBC.MEMBER_ID , BBC.TITLE , BBC.REG_DT , (SELECT REG_DT FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID LIMIT 1 ) AS COMMENT_REG_DT , if( (SELECT COUNT(*) FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID) > 0, 'Y', 'N' ) AS COMMENT_YN FROM BG_BOARD_CONTENTS BBC WHERE 1=1 AND BBC.BOARD_ID = #{board_id}
AND ( BBC.TITLE LIKE CONCAT('%', #{search}, '%'))
AND (SELECT COUNT(*) FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID) > 0
AND (SELECT COUNT(*) FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID) = 0
AND DATE_FORMAT(BBC.REG_DT,'%Y%m%d') >= #{search_start_date}
AND DATE_FORMAT(BBC.REG_DT,'%Y%m%d') <= #{search_end_date}
ORDER BY BBC.CONTENTS_ID ASC ) t, ( SELECT @RNUM := 0 ) R ORDER BY @RNUM DESC

SELECT /* boardQuery.contentsTotalCount */
	COUNT(BBC.CONTENTS_ID)
FROM BG_BOARD_CONTENTS BBC
WHERE 1=1
AND BBC.BOARD_ID = #{board_id}

	AND ( BBC.TITLE LIKE CONCAT('%', #{search}, '%'))


    
       AND (SELECT COUNT(*)
		FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID) > 0 
    
    
       AND (SELECT COUNT(*)
		FROM BG_BOARD_COMMENTS WHERE CONTENTS_ID = BBC.CONTENTS_ID) = 0 
    


	AND DATE_FORMAT(BBC.REG_DT,'%Y%m%d')  >=  #{search_start_date} 


	AND DATE_FORMAT(BBC.REG_DT,'%Y%m%d')  <=  #{search_end_date}


	LIMIT #{startNo}, #{perPage}

mapper.insert:
    INSERT INTO BG_BOARD_COMMENTS(
		CONTENTS_ID
		, MEMBER_ID
		, COMMENT
    )
    VALUES(
		#{board_contents_id}
		, #{member_id}
		, #{comment}	    
    )

    INSERT INTO BG_BOARD_CONTENTS(
		BOARD_ID
		, MEMBER_ID
		, TITLE
		, CONTENTS
		, IMG_PATH
    )
    VALUES(
    	#{board_id}
		, #{member_id}
		, #{title}
		, #{contents}	    
		, #{img_path}	    
    )

mapper.update:
UPDATE BG_BOARD_COMMENTS
SET 
	COMMENT = #{comment}
WHERE 1=1
AND COMMENT_ID = #{comment_id}
AND MEMBER_ID = #{member_id}

UPDATE BG_BOARD_CONTENTS
SET 
	TITLE = #{title}
	, CONTENTS = #{contents}
	, MOD_DT = now()
	, IMG_PATH = #{img_path}
WHERE 1=1
AND CONTENTS_ID = #{board_contents_id}
AND MEMBER_ID = #{member_id}

mapper.delete:
DELETE FROM BG_BOARD_COMMENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND COMMENT_ID = #{comment_id}

DELETE FROM BG_BOARD_COMMENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND CONTENTS_ID = #{board_contents_id}

DELETE FROM BG_BOARD_CONTENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND CONTENTS_ID = #{board_contents_id}
