mapper.insert:
INSERT INTO BG_BOARD_MASTER( 
	BOARD_NM,
	REG_MEMBER_ID,
	MOD_MEMBER_ID
	)
VALUES(
	#{board_nm},
	#{reg_member_id},
	#{mod_member_id}
)

mapper.select:
SELECT * FROM BG_BOARD_MASTER
WHERE 1=1
AND MASTER_ID = #{board_id}

mapper.delete:
DELETE FROM BG_BOARD_MASTER
WHERE 1=1
AND BOARD_NM = #{board_nm}
