mapper.select:
SELECT (@RNUM := @RNUM + 1) AS ROWNUM, t.* FROM( SELECT /* boardQuery.contentsListByBoardId */ BBC.CONTENTS_ID , BBC.MEMBER_ID , BBC.TITLE , BBC.REG_DT , BBC.CONTENTS_CODE , (SELECT CODE_DETAIL FROM BG_CODE WHERE CODE = BBC.CONTENTS_CODE) AS CONTENTS_CODE_DESC , BBC.EVENT_START_DT , BBC.EVENT_END_DT , BBC.SUB_CONTENTS , if(isnull(BBC.SUB_CONTENTS), 'N', 'Y') AS SUB_CONTENTS_YN , BBC.HITS , ifnull(BBC.STATUS, 1) AS STATUS , CASE (STATUS) WHEN 0 THEN '준비중' WHEN 1 THEN '진행중' WHEN 2 THEN '완료' ELSE '준비중' END AS STATUS_DESC , BBC.MOD_DT FROM BG_BOARD_CONTENTS BBC WHERE 1=1 AND BBC.BOARD_ID = #{board_id}
AND BBC.TITLE LIKE CONCAT('%', #{search} , '%')
AND BBC.MEMBER_ID LIKE CONCAT('%', #{search}, '%')
AND CONTENTS_CODE = #{contents_code}
AND BBC.SUB_CONTENTS IS NOT NULL
AND BBC.SUB_CONTENTS IS NULL
AND BBC.STATUS = #{status}
AND DATE_FORMAT(BBC.REG_DT,'%Y-%m-%d') >= #{search_start_date}
AND DATE_FORMAT(BBC.REG_DT,'%Y-%m-%d') <= #{search_end_date}
ORDER BY BBC.CONTENTS_ID ) t, ( SELECT @RNUM := 0 ) R ORDER BY @RNUM DESC
LIMIT #{startNo}, #{perPage}

SELECT /* boardQuery.contentsTotalCount */
	COUNT(BBC.CONTENTS_ID)
FROM BG_BOARD_CONTENTS BBC
WHERE 1=1
AND BBC.BOARD_ID = #{board_id}

	AND BBC.TITLE LIKE CONCAT('%', #{search}, '%')


	AND BBC.MEMBER_ID LIKE CONCAT('%', #{member_id}, '%')


	AND CONTENTS_CODE = #{contents_code}


    
       AND  BBC.SUB_CONTENTS IS NOT NULL
    
    
       AND  BBC.SUB_CONTENTS IS NULL
    


       AND  BBC.STATUS = #{status}


	AND DATE_FORMAT(BBC.REG_DT,'%Y-%m-%d')  >=  #{search_start_date} 


	AND DATE_FORMAT(BBC.REG_DT,'%Y-%m-%d')  <=  #{search_end_date}

mapper.insert:
    INSERT INTO BG_BOARD_COMMENTS(
		CONTENTS_ID
		, MEMBER_ID
		, COMMENT
    )
    VALUES(
		#{board_contents_id}
		, #{member_id}
		, #{comment}	    
    )

    INSERT INTO BG_BOARD_CONTENTS(
		BOARD_ID
		, MEMBER_ID
		, TITLE
		, CONTENTS
		, IMG_PATH
		, CONTENTS_CODE
		, EVENT_START_DT
		, EVENT_END_DT
		, SUB_CONTENTS
		, STATUS
    )
    VALUES(
    	#{board_id}
		, #{member_id}
		, #{title}
		, #{contents}	    
		, #{img_path}	    
		, #{contents_code}	    
		, #{event_start_dt}
		, #{event_end_dt}
		, #{sub_contents}
		, #{status}
    )

mapper.update:
UPDATE BG_BOARD_COMMENTS
SET 
	COMMENT = #{comment}
WHERE 1=1
AND COMMENT_ID = #{comment_id}
AND MEMBER_ID = #{member_id}

    UPDATE BG_BOARD_CONTENTS
    SET 
    	TITLE = #{title}
    	, CONTENTS = #{contents}
    	, MOD_DT = now()
    	, IMG_PATH = #{img_path}
		, CONTENTS_CODE = #{contents_code}	    
		, SUB_CONTENTS = #{sub_contents}
		, STATUS = #{status}
    WHERE 1=1
    AND CONTENTS_ID = #{board_contents_id}
    
	    AND MEMBER_ID = #{member_id}

mapper.delete:
DELETE FROM BG_BOARD_COMMENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND COMMENT_ID = #{comment_id}

DELETE FROM BG_BOARD_COMMENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND CONTENTS_ID = #{board_contents_id}

DELETE FROM BG_BOARD_CONTENTS
WHERE 1=1
AND MEMBER_ID = #{member_id}
AND CONTENTS_ID = #{board_contents_id}
