mapper.select:
SELECT * FROM BG_BRAND
WHERE BRAND_ID = #{brand_id}
AND DEL_YN = 'N'

mapper.insert:
		INSERT BG_BRAND(
			BRAND_NM,
			IMG_PATH,
			BRAND_DESC
   		)
   		VALUES(
   			#{brand_nm},
   			#{img_path},
   			#{brand_desc}
   		)

mapper.update:
UPDATE BG_BRAND SET
	BRAND_NM = #{brand_nm}
	, BRAND_DESC = #{brand_desc}
	, IMG_PATH = #{img_path}
	, MOD_DT = now()
WHERE 1=1
AND BRAND_ID = #{brand_id}
