mapper.update:
UPDATE BG_CATE SET CATE_LEVEL= #{cate_level}, CATE_NM = #{cate_nm},
PARENT_CATE_ID = #{parent_cate_id},
MOD_DT = now() WHERE 1=1 AND CATE_ID = #{cate_id}

mapper.delete:
DELETE FROM BG_CATE_ITEM_RELATIONSHIPS
WHERE 1=1
AND CATE_ID = #{cateId}

DELETE FROM BG_CATE
WHERE 1=1
AND CATE_ID = #{cateId}

DELETE FROM BG_CATE
WHERE 1=1
AND PARENT_CATE_ID = #{cateId}
