        BG_CODE
SET
        CODE_DETAIL = #{code_detail}
         ,CODE_LEVEL=#{code_level}
         ,PARENT_CODE=#{parent_code}
        , MOD_DT = now()
WHERE 1=1
AND CODE = #{code}

mapper.delete:
