mapper.insert:
    INSERT INTO BG_CATE_CONTENTS_RELATIONSHIPS(
    	CATE_ID
    	, CONTENTS_ID
    ) 
    VALUES (
		#{cate_id}
		, #{contents_id}	    	
    )

mapper.select:
SELECT COUNT(*) FROM BG_CATE_CONTENTS_RELATIONSHIPS
WHERE 1=1
AND CATE_ID = #{cate_id}
AND CONTENTS_ID = #{contents_id}

mapper.update:
UPDATE BG_CONTENTS SET
	CONTENTS_NM = #{contents_nm}
	, IMG_PATH = #{img_path}
	, SRC_PATH = #{src_path}
	
	, CONTENTS_SERIES_ID = #{contents_series_id}
	
	, STATUS = #{status}
	, CONTENTS_DESC = #{contents_desc}
	, MOD_DT = now()
	
WHERE 1=1
AND CONTENTS_ID = #{contents_id}
