mapper.select:
SELECT @RNUM := @RNUM + 1 AS ROWNUM, t.* FROM(
SELECT /* contenstQuery.list */ BCD.CONTENTS_NM , A.CONTENTS_ID , BB.BRAND_NM , BCS.CONTENTS_SERIES_NM , BCS.CONTENTS_SERIES_ID , BCD.CONTENTS_DESC , A.SRC_PATH , A.IMG_PATH , BCD.DISPLAY_YN , BCCR.CATE_ID , BB.BRAND_ID , DATE_FORMAT(A.REG_DT,'%Y-%m-%d %H:%i') AS REG_DT FROM BG_CONTENTS A, BG_CONTENTS_DETAIL BCD, BG_CATE_CONTENTS_RELATIONSHIPS BCCR, BG_CONTENTS_SERIES BCS, BG_BRAND BB WHERE 1=1 AND A.CONTENTS_ID = BCD.CONTENTS_ID AND BCD.IDX = BCCR.CONTENTS_DETAIL_IDX AND A.CONTENTS_SERIES_ID = BCS.CONTENTS_SERIES_ID AND BCS.BRAND_ID= BB.BRAND_ID
AND A.CONTENTS_ID = #{search}
AND BCD.CONTENTS_NM LIKE CONCAT('%', #{search}, '%')
AND BCD.DISPLAY_YN = #{display_yn}
AND BB.BRAND_ID = #{brand_id}
AND BCCR.CATE_ID = #{category_id}
AND BCS.CONTENTS_SERIES_ID = #{series_id}
AND DATE_FORMAT(A.REG_DT,'%Y%m%d') >= #{search_start_date}
AND DATE_FORMAT(A.REG_DT,'%Y%m%d') <= #{search_end_date}
GROUP BY A.CONTENTS_ID ORDER BY A.CONTENTS_ID ASC ) t, ( SELECT @RNUM := 0 ) R ORDER BY @RNUM DESC
LIMIT #{startNo}, #{perPage}

mapper.update:
UPDATE BG_CATE_CONTENTS_RELATIONSHIPS
SET ORDERING_NO = #{ordering_no}
WHERE 1=1 
AND CATE_ID = #{category_id}
AND CONTENTS_ID = #{contents_id}
