mapper.insert:
   		INSERT BG_ITEM(
			ITEM_NM,
			GROUP_ID,
			SRC_PATH   		
   		)
   		VALUES(
   			#{item_nm},
   			#{group_id},
   			#{src_path}
   		)

mapper.update:
UPDATE BG_ITEM SET
	ITEM_NM = #{item_nm}
	, SRC_PATH = #{src_path}
	, GROUP_ID = #{group_id}
	, MOD_DT = now()
WHERE 1=1
AND ITEM_ID = #{item_id}
