mapper.select:
SELECT * FROM BG_SERIES
WHERE 1=1
AND IDX = #{series_idx}

mapper.insert:
		INSERT BG_SERIES(
			BRAND_ID,
			CATE_ID,
			SERIES_NM
   		)
   		VALUES(
   			#{brand_id},
   			#{cate_id},
   			#{series_nm}
   		)

mapper.update:
UPDATE BG_SERIES SET
	SERIES_NM = #{series_nm},

	BRAND_ID = #{brand_id}


	CATE_ID = #{cate_id}

WHERE 1=1
AND IDX = #{series_idx}

mapper.delete:
DELETE FROM BG_SERIES
WHERE 1=1
AND IDX = #{series_idx}
