mapper.insert:
		INSERT INTO BG_MEMBER (
		MEMBER_ID,
		MEMBER_NM,
		MEMBER_PW
		)
		VALUES (
        #{ id },
        #{ name },
        #{ pw }
		)

mapper.update:
UPDATE BG_MEMBER

id = #{ id },
name = #{ name },
pw = #{ pw }

WHERE = id = #{ id }

mapper.delete:
	DELETE from USER
	WHERE id = #{ id }
    LIMIT = 1
