mapper.select:
SELECT @RNUM := @RNUM + 1 AS ROWNUM, t.* FROM(
SELECT /* userQuery.list */ IDX , MEMBER_ID , MEMBER_NM , MEMBER_CEL , MEMBER_ROLE , MEMBER_STATUS , DATE_FORMAT(REG_DT,'%Y-%m-%d %H:%i') AS REG_DT , case (MEMBER_ROLE) when 0 then '무료회원' when 1 then '유료회원' else '무료회원' end AS MEMBER_ROLE_DESC , case (MEMBER_STATUS) when 0 then '이용' when 1 then '중지' when 2 then '탈퇴' end AS MEMBER_STATUS_DESC FROM BG_MEMBER WHERE 1=1
AND ( MEMBER_ID LIKE CONCAT('%', #{search}, '%'))
AND ( MEMBER_NM LIKE CONCAT('%', #{search}, '%'))
AND ( REPLACE(MEMBER_CEL, '-', '') LIKE CONCAT('%', #{search}, '%'))
AND MEMBER_ROLE = #{member_role}
AND DATE_FORMAT(REG_DT,'%Y-%m-%d') >= #{search_start_date}
AND DATE_FORMAT(REG_DT,'%Y-%m-%d') <= #{search_end_date}
ORDER BY IDX ASC ) t, ( SELECT @RNUM := 0 ) R ORDER BY @RNUM DESC
LIMIT #{startNo}, #{perPage}

SELECT /* userQuery.listCount */
	COUNT(*) AS LIST_COUNT
FROM BG_MEMBER 
WHERE 1=1 

	
		AND ( MEMBER_ID LIKE CONCAT('%', #{search}, '%'))
	
	
		AND ( MEMBER_NM LIKE CONCAT('%', #{search}, '%'))
	
	
		AND ( REPLACE(MEMBER_CEL, '-', '')  LIKE CONCAT('%', #{search}, '%'))
	


    AND MEMBER_ROLE = #{member_role}


	AND DATE_FORMAT(REG_DT,'%Y-%m-%d')  >=  #{search_start_date} 


	AND DATE_FORMAT(REG_DT,'%Y-%m-%d')  <=  #{search_end_date}

mapper.update:
UPDATE BG_MEMBER 
SET
	MEMBER_NM = #{member_name}
	, MEMBER_EMAIL = #{member_email}
	, MEMBER_CEL = #{member_cel}
	, MEMBER_ADDR_1 = #{member_addr_1}
	, MEMBER_ADDR_2 = #{member_addr_2}
	
WHERE 1=1
AND MEMBER_ID = #{member_id}

UPDATE BG_CHILDREN
SET
	CHILD_NM = #{child_name}
	, PHOTO = #{img_path}
	, GENDER = #{gender}
	, BIRTH_YEAR = #{birth_year}
	, BIRTH_MONTH = #{birth_month}
	, BIRTH_DAY = #{birth_day}
WHERE 1=1
AND IDX = #{child_idx}
